@pytest.mark.parametrize("grid, born, survivor, live_cell_count, answer", update_grid_cell_status)
def test_update_grid(grid, born, survivor, live_cell_count, answer):
    assert game.update_grid(grid, born, survivor, live_cell_count) == answer


def test_draw_game_buttons():
    button_1, button_2 = game.draw_game_buttons("Start")
    assert isinstance(button_1, pygame.Rect) and isinstance(button_2, pygame.Rect)


# mock fixture in conftest.py
def test_draw(game_grid, mock_update, mock_rect):
    game_grid[25][2] = 1
    game_grid[20][40] = 1
    game.draw(game_grid, 2, 0)
    mock_update.assert_called_once()
    assert mock_rect.call_count == 5000


def test_random_game_generation(game_grid):
    grid, live_cell_count = game.random_game_generation(game_grid, 0)
    count = 0
    for row in grid:
        count += row.count(1)
    assert live_cell_count == count
    assert 20 <= live_cell_count <= 300
    grid, live_cell_count = game.random_game_generation(grid, live_cell_count)
    max_count = 300 + count
    count = 0
    for row in grid:
        count += row.count(1)
    assert live_cell_count == count
    assert 0 <= live_cell_count <= max_count


# mock fixture in conftest.py
def test_game(mock_random_game_generation, mock_changing_cell_status, mock_update_grid, mock_draw):
    pygame.init()

    # Post a MOUSEBUTTONDOWN events to simulate a click.
    # Start
    start_event = pygame.event.Event(pygame.MOUSEBUTTONDOWN, pos=(300, 650))
    pygame.event.post(start_event)
    # nothing
    nothing_event = pygame.event.Event(pygame.MOUSEBUTTONDOWN, pos=(15, 685))
    pygame.event.post(nothing_event)
    pygame.event.post(nothing_event)
    # changing_cell_status - single cell
    single_cell_event = pygame.event.Event(pygame.MOUSEBUTTONDOWN, pos=(246, 248))
    pygame.event.post(single_cell_event)
    pygame.event.post(single_cell_event)
    # random_game_generation - mock
    random_game_generation_event = pygame.event.Event(pygame.MOUSEBUTTONDOWN, pos=(515, 676))
    pygame.event.post(random_game_generation_event)
    # changing_cell_status - single cell
    single_cell_event = pygame.event.Event(pygame.MOUSEBUTTONDOWN, pos=(247, 259))
    pygame.event.post(single_cell_event)
    single_cell_event = pygame.event.Event(pygame.MOUSEBUTTONDOWN, pos=(269, 246))
    pygame.event.post(single_cell_event)
    pygame.event.post(start_event)
    pygame.event.post(nothing_event)
    pygame.event.post(start_event)
    pygame.event.post(start_event)

    # Post a QUIT event to exit the loop
    pygame.event.post(pygame.event.Event(pygame.QUIT))

    # Run the main loop to process the events
    with patch('game_of_life.game.main', side_effect=game.main) as mock_main:
        mock_main()

    mock_random_game_generation.assert_called()
    mock_changing_cell_status.assert_called()
    mock_update_grid.assert_called()
    mock_draw.assert_called()

    pygame.quit()


@patch('game_of_life.game.game')
def test_game_of_life(mock_game):
    game.game_of_life("C36/Q23")
    mock_game.assert_called_with(3, [2, 3])
    game.game_of_life("B4/S19")
    mock_game.assert_called_with(4, [1, 9])


@patch('game_of_life.game.game')
def test_main(mock_game):
    game.main()
    mock_game.assert_called_once_with(3, [2, 3])
